```java
import java.lang.reflect.Field;
import java.lang.reflect.Method;
import java.lang.reflect.Modifier;
import java.lang.reflect.Parameter;

public class Test11 {
    public static void main(String[] args) throws Exception {
        StringBuilder s=new StringBuilder();
/*Á±ªÂ£∞Êòé */
Class aClass=Class.forName("com.reflect.test.Person");
        String p1= Modifier.toString(aClass.getModifiers());//‰øÆÈ•∞Á¨¶
String c1=aClass.getSimpleName();//Ëé∑ÂèñÁÆÄÂçïÁ±ªÂêç
s.append(p1+" class "+c1+"{\n");
/*Ëé∑ÂèñÂ≠óÊÆµ */
Field[] fields=aClass.getDeclaredFields();
        for (Field f:fields
             ) {
//Ëé∑ÂèñÂ≠óÊÆµ‰øÆÈ•∞Á¨¶
s.append("\t");
            s.append(Modifier.toString(f.getModifiers()));
            s.append(" ");
//Ëé∑ÂèñÂ≠óÊÆµÁ±ªÂûã
s.append(f.getType().getSimpleName());
            s.append(" ");
//Ëé∑ÂèñÂ≠óÊÆµÂêçÁß∞
s.append(f.getName());
            s.append(";\n");
        }

/*Ëé∑ÂèñÊñπÊ≥ï */
Method[] methods=aClass.getDeclaredMethods();
        for (Method m:methods
             ) {
//Ëé∑ÂèñÊñπÊ≥ï‰øÆÈ•∞Á¨¶
s.append("\t");
            s.append(Modifier.toString(m.getModifiers()));
            s.append(" ");
//Ëé∑ÂèñÊñπÊ≥ïËøîÂõûÂÄºÁ±ªÂûã
s.append(m.getReturnType());
            s.append(" ");
//Ëé∑ÂèñÊñπÊ≥ïÂêçÁß∞
s.append(m.getName());
            s.append("(");
//Ëé∑ÂèñÂΩ¢ÂèÇÂàóË°®
Class[] parameters=m.getParameterTypes();
            for (Class c:parameters
                 ) {
                s.append(c.getSimpleName());
                s.append(",");
            }
            s.deleteCharAt(s.length()-1);//Âà†Èô§Âõ†‰∏∫Âæ™ÁéØÂ§öÂÜôÁöÑÈÇ£‰∏™ÈÄóÂè∑
s.append("){}\n");//ÊñπÊ≥ï‰ΩìÁªìÂ∞æ
}

/*Á±ªÂ£∞ÊòéÁªìÂ∞æ */
s.append("}");

        System.out.println(s);
    }
}

```

