class Producer implements Runnable {

    private List lists;// ‰ªìÂ∫ì

    public Producer(List lists) {
        this.lists = lists;
    }

    @Override
    public void run() {
        // ‰∏ÄÁõ¥Áîü‰∫ß
        while (true) {
            synchronized (lists) {
                // ‰ªìÂ∫ì‰∏≠ÊòØÂê¶ÊúâË¥ß
                if (lists.size() > 0) {
                    // ÊúâË¥ßÂ∞±Á≠âÂæÖÔºåÈáäÊîæÊéâlistÁöÑÈîÅ
                    try {
                        lists.wait();
                    } catch (InterruptedException e) {
                        e.printStackTrace();
                    }
                }
                // ËÉΩËøêË°åÂà∞ËøôÈáåÔºåËØ¥Êòé‰ªìÂ∫ìÊ≤°Ë¥ß‰∫ÜÔºåÈÇ£Â∞±ÂºÄÂßãÂà∂ÈÄ†
                Object o = new Object();
                //Âà∂ÈÄ†ÁöÑÊó∂ÂÄôÔºåÈÉΩÊÉ≥ÂΩìÊáíÁãóÔºåÂ∞±Ê≠á‰∏Ä‰ºö
                try {
                    lists.wait(1000);
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
                lists.add(o);
                //Âä†‰∏™Êó∂Èó¥Ôºå‰∏çÁÑ∂‰∏çÂ•ΩÁúã
                SimpleDateFormat gs=new SimpleDateFormat("HH:mm:ss SSS");
                String now=gs.format(new Date());

                System.out.println(now+"-"+Thread.currentThread().getName() + "Âà∂ÈÄ†Ë¥ßÁâ©Ôºö" + o);
                lists.notify();
            }

        }
    }
}

// Ê∂àË¥πËÄÖ
class Consumer implements Runnable {
    private List lists;// ‰ªìÂ∫ì

    public Consumer(List lists) {
