        // ‰∏ÄÁõ¥Ê∂àË¥π

        while (true) {
            synchronized (lists) {
                // Âà§Êñ≠ÊòØÂê¶‰ªìÂ∫ìÊòØÂê¶ÊúâË¥ß
                if (lists.size() == 0) {
                    // ‰ªìÂ∫ìÊ≤°Ë¥ßÔºåÂ∞±‰∏çËÉΩÊ∂àË¥π‰∫ÜÔºåÁ≠âÂæÖÁîü‰∫ßËÄÖÁîü‰∫ßÔºåÈáäÊîæÊéâÈîÅ
                    try {
