@Override  
public void update(User user) {  
    ObjectInputStream ois = null;  
    ObjectOutputStream oos = null;  
    List<User> list = null;  
    try {  
        //    ËØªÂèñÊú¨Âú∞Áî®Êà∑‰ø°ÊÅØ  
