    /**  
     * @param userLoginInfo ‰º†ÂÖ•ÁöÑÁî®Êà∑‰ø°ÊÅØÈõÜÂêà  
     * @return ËøîÂõûÊòØÂê¶ÁôªÂΩïÊàêÂäüÁöÑÁªìÊûú  
     */  
    private static boolean login(Map<String, String> userLoginInfo) {  
        // ÂàùÂßãÂåñÁôªÂΩïÁªìÊûú  
        boolean loginSucceed = false;  
        //    Ëé∑ÂèñÁî®Êà∑ËæìÂÖ•ÁöÑ‰ø°ÊÅØ  
        String loginName = userLoginInfo.get("loginName");  
        String loginPassword = userLoginInfo.get("loginPassword");  

        // JDBCÈÉ®ÂàÜ  
        // ÂÆö‰πâÂü∫Êú¨ÁöÑËøûÊé•ÂèòÈáè  
        Connection connection = null;// Âª∫Á´ãsqlÊï∞ÊçÆÂ∫ìÁöÑËøûÊé•  
        Statement statement = null;// sqlÊâßË°åËØ≠Âè•ÁöÑÂèëÈÄÅËØ≠Âè•  
        ResultSet resultSet = null;// ËøîÂõûÁªìÊûúÁöÑÈõÜÂêà  
        try {  
            // 1.Ê≥®ÂÜåÈ©±Âä®  
            Class.forName("com.mysql.jdbc.Driver");  
            // 2.Ëé∑ÂèñËøûÊé•  
            connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/bjpowernode", "root", "root");  
            // 3.ÂàõÂª∫Êï∞ÊçÆÂ∫ìÊìç‰ΩúÂØπË±°  
            statement = connection.createStatement();  
            // 4.sqlÊâßË°åËØ≠Âè•  
            String sql = "select * from t_user where loginName='" + loginName + "' and loginPassword='" + loginPassword + "'";  
            // int count=statement.executeUpdate(sql);//ÂΩ±ÂìçÁöÑÊù°Êï∞ÔºåÊ≠§Â§ÑÊó†Áî®  
            resultSet = statement.executeQuery(sql);// ÊâßË°åslqÔºåÂπ∂ËøîÂõûÁªìÊûúÈõÜ  
            // 5.Â§ÑÁêÜÁªìÊûúÈõÜ  
            // Â¶ÇÊûúÊúâËøîÂõûÁöÑËÆ∞ÂΩïÔºåËØ¥ÊòéÊï∞ÊçÆ‰∏≠Â≠òÂú®ËøôÊ†∑ÁöÑÁî®Êà∑‰ø°ÊÅØÔºåÂ∞±ËÆæÁΩÆtrue  
            if (resultSet.next()) {  
                loginSucceed = true;  
            }  

        } catch (ClassNotFoundException e) {  
            e.printStackTrace();  
        } catch (SQLException e) {  
            e.printStackTrace();  
        } finally {  
            // 6ÈáäÊîæËµÑÊ∫ê  
            if (resultSet != null) {  
                try {  
                    resultSet.close();  
                } catch (SQLException e) {  
                    throw new RuntimeException(e);  
                }  
            }  

            if (statement != null) {  
                try {  
                    statement.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
            if (connection != null) {  
                try {  
                    connection.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
        }  
        return loginSucceed;  
    }  

    /**  
