import java.util.Scanner;  

public class JDBCTest07 {  
    public static void main(String[] args) {  
    //1.  ÂàùÂßãÂåñÁïåÈù¢  
        Map<String,String> userLoginInfo=initUI();  
    //2.  È™åËØÅÁî®Êà∑‰ø°ÊÅØ  
        boolean loginSucceed=login(userLoginInfo);  
    //3.  ËæìÂá∫ÁªìÊûú‰ø°ÊÅØ  
        System.out.println(loginSucceed?"ÁôªÂΩïÊàêÂäü":"ÁôªÂΩïÂ§±Ë¥•");  
    }  

    /**  
     * È™åËØÅÁî®Êà∑ÁôªÂΩï‰ø°ÊÅØ  
     * @param userLoginInfo Áî®Êà∑ËæìÂÖ•ÁöÑÁî®Êà∑‰ø°ÊÅØ  
     * @return ËøîÂõûÁôªÂΩïÁöÑÁªìÊûú  
     */  
    private static boolean login(Map<String, String> userLoginInfo) {  
        //Ëé∑ÂèñÁî®Êà∑ÁöÑÁôªÂΩï‰ø°ÊÅØ  
        String loginName=userLoginInfo.get("loginName");  
        String loginPassword=userLoginInfo.get("loginPassword");  
        //‰∏ésqlÊúçÂä°Âô®ËøõË°åÈ™åËØÅ  
        boolean loginSucceed=false;// ÁôªÂΩïÁªìÊûúÊòØÂê¶ÊàêÂäüÁöÑÊ†áÁ≠æ  
        Connection connection=null;//ËøûÊé•ÁöÑÂØπË±°  
        PreparedStatement preparedStatement=null;//Êìç‰ΩúsqlÊï∞ÊçÆÂ∫ìÁöÑÂØπË±°ÔºåÈÅøÂÖçsqlÊ≥®ÂÖ•ÁöÑÊñπÂºè  
        ResultSet resultSet=null;  
        try {  
            // 1.Ê≥®ÂÜåÈ©±Âä®  
            Class.forName("com.mysql.jdbc.Driver");  
            // 2.Ëé∑ÂèñËøûÊé•  
            connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/bjpowernode", "root", "root");  
            // 3.ÂàõÂª∫sqlÊìç‰ΩúÂØπË±°  
            String sql="select * from t_user where loginName = ? and loginPassword = ?";  
            //ÂèëÈÄÅÁöÑsqlÊ°ÜÂ≠êÁªôDBMSÔºåDBMSËøõË°åsqlËØ≠Âè•ÁöÑÈ¢ÑÁºñËØëÔºå  
            // ‰πãÂêéÁªôÔºü‰º†ÂÄºÂç≥ÂèØÂ∞±‰∏ç‰ºöËÆ≤‰º†ÁöÑÂÄº‰Ωú‰∏∫ÁºñËØëÁöÑ‰∏ÄÈÉ®ÂàÜÔºåÈÄ†ÊàêsqlÊ≥®ÂÖ•  
            preparedStatement=connection.prepareStatement(sql);  
            //ËÆæÁΩÆsqlËØ≠Âè•‰∏≠ÔºüÁöÑÂÄº  
            preparedStatement.setString(1,loginName);  
            preparedStatement.setString(2,loginPassword);  
            //ÊâßË°åsql  
            resultSet=preparedStatement.executeQuery();  
            if (resultSet.next()){  
                loginSucceed=true;  
            }  
        } catch (ClassNotFoundException e) {  
            e.printStackTrace();  
        } catch (SQLException e) {  
            e.printStackTrace();  
        }finally {  
            if (resultSet!=null){  
                try {  
                    resultSet.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
            if (preparedStatement!=null){  
                try {  
                    preparedStatement.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
            if (connection!=null){  
                try {  
                    connection.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
        }  
        return loginSucceed;  
    }  

    /**  
     * Áî®Êà∑ÁôªÂΩïÁöÑÁïåÈù¢  
     * @return ËøîÂõûÁî®Êà∑Â°´ÂÜôÁöÑÁôªÂΩï‰ø°ÊÅØÈõÜÂêà  
     */  
    private static Map<String, String> initUI() {  
        Scanner s=new Scanner(System.in);  
        String loginName,loginPassword;  
        System.out.println("ËØ∑ËæìÂÖ•Áî®Êà∑ÂêçÔºö");  
        loginName=s.nextLine();  
        System.out.println("ËØ∑ËæìÂÖ•  ÂØÜÁ†ÅÔºö");  
        loginPassword=s.nextLine();  

        //        ÊîæÂÖ•Âà∞ÈõÜÂêà‰∏≠  
        Map<String,String> userLoginInfoSet=new HashMap<>();  
        userLoginInfoSet.put("loginName",loginName);  
        userLoginInfoSet.put("loginPassword",loginPassword);  
        return userLoginInfoSet;  
    }  
}
