        //ÊääÂèñÂá∫ÁöÑ1000ÊîæÂÖ•Âà∞Âè¶‰∏Ä‰∏™Ë¥¶Êà∑  
            //‰º†ÂÄºËøõË°åÊü•ËØ¢  
            ps.setInt(1,1000);  
            ps.setInt(2,222);  
            count+=ps.executeUpdate();//ÊâßË°åÂπ∂ËøîÂõûÂΩ±ÂìçÊù°Êï∞  
            System.out.println(count==2?"ËΩ¨Ë¥¶ÊàêÂäü":"ËΩ¨Ë¥¶Â§±Ë¥•");  
            // System.out.println(count);  
        //    Êèê‰∫§‰∫ãÂä°  
            con.commit();  
        } catch (Exception e) {  
            //Â¶ÇÊûúÊâßË°åËøáÁ®ã‰∏≠Âá∫Áé∞‰∫ÜÂºÇÂ∏∏ÔºåÂ∞±ÂõûÊªö‰∫ãÂä°  
            if (con!=null){  
                try {  
