                    e.printStackTrace();  
                }  
            }  
            e.printStackTrace();  
        }finally {  
        //    ÈáäÊîæËµÑÊ∫ê  

            if (ps!=null){  
                try {  
                    ps.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
            DBUtil.close(con,null,null);  
        }  
    }  
}
