package jdbc;  

import java.sql.*;  

public class DBUtil {  
    //‰∏çÈúÄË¶ÅnewÂØπË±°ÔºåÁõ¥Êé•Ë∞ÉÁî®Â∑•ÂÖ∑Á±ª‰∏≠ÁöÑÂÖ¨ÂºÄÈùôÊÄÅÊñπÊ≥ïÂç≥ÂèØ  
    private DBUtil() {  

    }  

    /**  
     * ÈùôÊÄÅ‰ª£Á†ÅÂùóÔºåÂä†ËΩΩÁ±ªÁöÑÊó∂ÂÄôÊâßË°å‰∏ÄÊ¨°ÔºåÂêéÈù¢Â∞±ÈúÄË¶ÅÊâßË°å‰∫Ü  
     * Áî®‰∫éÊ≥®ÂÜåÈ©±Âä®  
     */  
    static {  
