public class ArraySearch02 {  
    public static void main(String[] args) {  
        int[] arr={1,45,56,78,89,455,1254,4546};  
        int index=arrBinSearch(arr,78);  
        System.out.println(index==-1?"ËØ•ÂÖÉÁ¥†‰∏çÂ≠òÂú®":"ËØ•ÂÖÉÁ¥†ÁöÑ‰∏ãÊ†áÊòØ"+index);  
        index=arrBinSearch(arr,1);  
        System.out.println(index==-1?"ËØ•ÂÖÉÁ¥†‰∏çÂ≠òÂú®":"ËØ•ÂÖÉÁ¥†ÁöÑ‰∏ãÊ†áÊòØ"+index);  
    }  

    public static int arrBinSearch(int[] arr, int ele) {  
    //    ÂÆö‰πâÂ§ßÊï∞,Â∞èÊï∞,‰∏≠Èó¥Êï∞ÁöÑ‰∏ãÊ†á  
        int first=0;  
        int last=arr.length-1;  
        int mid=0;  

        //ÂΩìÂ∞èÊï∞Á≠â‰∫éÂ∞è‰∫éÂêéÈù¢ÁöÑÂ§ßÊï∞ÊâçËøêË°å  
        while (first<=last){  
            mid=(last+first)/2;  

            if (ele>arr[mid]){               //Â¶ÇÊûúÊü•ÊâæÁöÑÂÖÉÁ¥†Â§ß‰∫é‰∏≠Èó¥Êï∞,ÈÇ£‰πàÂ∞±firstÂ∞±Ë∞ÉÊï¥‰∏∫midÁöÑ‰∏ã‰∏Ä‰∏™ÂÖÉÁ¥†,Âú®Âè≥ÂçäÂå∫ÂüüÁªßÁª≠‰∫åÂçäÊü•Êâæ  
                first=mid+1;  
            } else if (ele<arr[mid]) {          //Â¶ÇÊûúÊü•ÊâæÁöÑÂÖÉÁ¥†Â∞è‰∫é‰∏≠Èó¥Êï∞,ÈÇ£‰πàÂ∞±lastÂ∞±Ë∞ÉÊï¥‰∏∫midÁöÑ‰∏ã‰∏ä‰∏™ÂÖÉÁ¥†,Âú®Â∑¶ÂçäÂå∫ÂüüÁªßÁª≠‰∫åÂçäÊü•Êâæ  
                last=mid-1;  
            } else {                //Â¶ÇÊûúÂàöÂ•ΩÁõ∏Á≠â,Â∞±Áõ¥Êé•ËøîÂõû‰∏ãÊ†á  
                return mid;  
            }  
        }  

        return -1;  
    }  
}
