        eles[index]=obj;  
        System.out.println("push success"+obj+" frameÔºö"+index);  
    }  

    //ÂºπÊ†àÊñπÊ≥ï  
    public void pop() throws StatckOperationException{  
        if (index<0){  
            throw new StatckOperationException("ÂºπÊ†àÂ§±Ë¥•");  
        }  
        System.out.println("pop"+eles[index]);  
        index--;  
        System.out.println("stack iframe to: "+index);  
    }  

    //Â∞ÅË£Ö  

    public MyStack() {  
        this.eles=new Object[10];//ÂàùÂßãÂåñÁ´ôÂÆπÈáè  
        this.index=-1;//Ê†àÂ∏ßÂú®Â∫ïÈÉ®  
    }  

    public MyStack(Object[] eles, int index) {  
        this.eles = eles;  
        this.index = index;  
    }  

    public Object[] getEles() {  
        return eles;  
    }  

    public void setEles(Object[] eles) {  
        this.eles = eles;  
    }  

    public int getIndex() {  
        return index;  
    }  

    public void setIndex(int index) {  
        this.index = index;  
    }  
}
