package com.bjpowernode.dao.Impl;  

import com.bjpowernode.bean.PathConstant;  
import com.bjpowernode.bean.User;  
import com.bjpowernode.dao.UserDao;  

import java.io.*;  
import java.util.ArrayList;  
import java.util.List;  

public class UserDaoImpl implements UserDao {  
    /**  
     * 选择  
     *  
     * @return {@link List}<{@link User}>  
     */    @Override  
    public List<User> select() {  
        try (ObjectInputStream ois = new ObjectInputStream(new FileInputStream(PathConstant.User_Path))) {  
            //    读取  
            List<User> list = (List<User>) ois.readObject();  
            return list;  
        } catch (Exception e) {  
            // throw new RuntimeException(e);  
            e.printStackTrace();  
        }  
        // 如果异常，就返回一个无用户对象的空列表  
        return new ArrayList<>();  
    }  

    /**  
     * 添加  
     *  
     * @param user 用户  
     */  
    @Override  
    public void add(User user) {  
        ObjectInputStream ois = null;  
        ObjectOutputStream oos = null;  
        List<User> list = null;  
        try {  
            //    读取硬盘中的用户信息  
            ois = new ObjectInputStream(new FileInputStream(PathConstant.User_Path));  

            list = (List<User>) ois.readObject();// 读取用户信息  
            // 判断list是否有  
            if (list != null) {  
                // 设置新添加的用户id  
                User lastUser = list.get(list.size() - 1);  
                user.setId(lastUser.getId() + 1);  
                //    添加到list中  
                list.add(user);  
            } else {  
                //    当list为空的时候，就需要一个空的去装  
                list = new ArrayList<>();  
                user.setId(0001);  
                list.add(user);  

            }  
            //    持久化到本地硬盘  
            oos = new ObjectOutputStream(new FileOutputStream(PathConstant.User_Path));  

            oos.writeObject(list);  
            oos.flush();  

        } catch (Exception e) {  
            throw new RuntimeException("添加用户失败");  
        } finally {  
            // 释放资源  
            try {  
                if (ois != null) {  
                    ois.close();  
                }  
                if (oos != null) {  
                    oos.close();  
                }  
            } catch (IOException e) {  
                e.printStackTrace();  
            }  
        }  
    }  
}
