# 0009_案例：连接数据库，验证用户登录(可被sql注入）
```java
import java.sql.*;  
import java.util.HashMap;  
import java.util.Map;  
import java.util.Scanner;  

public class JDBCTest06 {  
    public static void main(String[] args) {  
        // 1.初始化用户界面  
        Map<String, String> userLoginInfo = initUI();// 返回用户名和密码  
        //2.用户登录，验证用户名和密码  
        boolean loginSucceed = login(userLoginInfo);  

        //3.输出结果  
        System.out.println(loginSucceed?"登录成功":"登录失败");  
    }  

    /**  
     * @param userLoginInfo 传入的用户信息集合  
     * @return 返回是否登录成功的结果  
     */  
    private static boolean login(Map<String, String> userLoginInfo) {  
        // 初始化登录结果  
        boolean loginSucceed = false;  
        //    获取用户输入的信息  
        String loginName = userLoginInfo.get("loginName");  
        String loginPassword = userLoginInfo.get("loginPassword");  

        // JDBC部分  
        // 定义基本的连接变量  
        Connection connection = null;// 建立sql数据库的连接  
        Statement statement = null;// sql执行语句的发送语句  
        ResultSet resultSet = null;// 返回结果的集合  
        try {  
            // 1.注册驱动  
            Class.forName("com.mysql.jdbc.Driver");  
            // 2.获取连接  
            connection = DriverManager.getConnection("jdbc:mysql://localhost:3306/bjpowernode", "root", "root");  
            // 3.创建数据库操作对象  
            statement = connection.createStatement();  
            // 4.sql执行语句  
            String sql = "select * from t_user where loginName='" + loginName + "' and loginPassword='" + loginPassword + "'";  
            // int count=statement.executeUpdate(sql);//影响的条数，此处无用  
            resultSet = statement.executeQuery(sql);// 执行slq，并返回结果集  
            // 5.处理结果集  
            // 如果有返回的记录，说明数据中存在这样的用户信息，就设置true  
            if (resultSet.next()) {  
                loginSucceed = true;  
            }  

        } catch (ClassNotFoundException e) {  
            e.printStackTrace();  
        } catch (SQLException e) {  
            e.printStackTrace();  
        } finally {  
            // 6释放资源  
            if (resultSet != null) {  
                try {  
                    resultSet.close();  
                } catch (SQLException e) {  
                    throw new RuntimeException(e);  
                }  
            }  

            if (statement != null) {  
                try {  
                    statement.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
            if (connection != null) {  
                try {  
                    connection.close();  
                } catch (SQLException e) {  
                    e.printStackTrace();  
                }  
            }  
        }  
        return loginSucceed;  
    }  

    /**  
     * 初始化用户界面  
     *  
     * @return 返回用户账户和密码的map集合  
     */  
    private static Map<String, String> initUI() {  
        // 输入  
        Scanner s = new Scanner(System.in);  
        System.out.println("请输入用户名：");  
        String loginName = s.nextLine();  
        System.out.println("请输入密码");  
        String loginPassword = s.nextLine();  
        // 存放到map集合中  
        Map<String, String> userLoginInfo = new HashMap<>();  
        userLoginInfo.put("loginName", loginName);  
        userLoginInfo.put("loginPassword", loginPassword);  
        // 返回账户信息集合  
        return userLoginInfo;  
    }  


}
