    public Customer() {
        this.name="未知";
        this.act=new Account("账户1",0,0.01);
    }
    public Customer(String name, Account act) {
        this.name = name;
        this.act = act;
    }
    //get and set封装
    public String getName() {
        return name;
    }
    public void setName(String name) {
        this.name = name;
    }
    public Account getAct() {
        return act;
    }
    public void setAct(Account act) {
        this.act = act;
    }

}

