class Student {  
    private int no;  
    private String name;  

    //重写equals方法来判断两个学生是否是一个学生  
    //通过比较两个学生的姓名和学号来判断  
    //重写toString 方法  
    public String toString() {  
        return no + name + "";  
    }  

    //方法覆盖  
    public boolean equals(Object obj) {  
        if (obj == null || !(obj instanceof Student)) {  
            return false;  
        }  
        if (this == obj) return true;  
        Student s=(Student) obj;  
        return (this.name.equals(s.name) && this.no==s.no);  //因为姓名是string类型的，所以还是需要用equals来对字符进行比较，这个equals是调用的string的equals，不是Student类型对象的equals
    }  



    //constructor  
    public Student() {  
    }  

    public Student(int no, String name) {  
        this.no = no;  
        this.name = name;  
    }  

