```java  
public class Hotel {  
    private Room[][] rooms;  

    //对一个酒店进行基本的构造  
    public Hotel() {  
        rooms = new Room[3][10];  
        for (int i = 0; i < rooms.length; i++) {  
            for (int j = 0; j < rooms[i].length; j++) {  
                if (i == 0) {  
                    rooms[i][j] = new Room((i + 1) * 100 + j + 1, "单人间", true);  
                } else if (i == 1) {  
                    rooms[i][j] = new Room((i + 1) * 100 + j + 1, "标准间", true);  
                } else {  
                    rooms[i][j] = new Room((i + 1) * 100 + j + 1, "豪华间", true);  
                }  
            }  
        }  
    }  

    public Hotel(Room[][] rooms) {  
        this.rooms = rooms;  
    }  
//    //重写toString方法，将所有的房间返回  
    @Override  
    public String toString() {  
        String s="";  
        for (int i = 0; i < rooms.length; i++) {  
            for (int j = 0; j < rooms[i].length; j++) {  
                s+=rooms[i][j]+" ";  
            }  
                s+="\n";  
        }  
        return s;  
    }  
//    打印空闲的房间  
    public void dayin(){  
        for (int i = 0; i < rooms.length; i++) {  
            for (int j = 0; j < rooms[i].length; j++) {  
                if (rooms[i][j].isStatus()==true){  
                    System.out.println(rooms[i][j]);  
                }  
            }  
            System.out.println();  
        }  
    }  

//    订房  
    public void order(int roomNo){  
        Room room=rooms[roomNo/100-1][roomNo%100-1];//获取房间对象的数组下标  
        if (room.isStatus()==true){  
            room.setStatus(false);  
            System.out.println(room.getNo()+"订房成功");  
        }else  
            System.out.println(room.getNo()+"已被其他人订房");  
    }  
    //退房  
    public void exit(int roomNo){  
        Room room=rooms[roomNo/100-1][roomNo%100-1];//获取房间对象的数组下标  
        if (room.isStatus()==false){  
            room.setStatus(true);  
            System.out.println(room.getNo()+"退房成功");  
        }else  
            System.out.println(room.getNo()+"未被订房，退房失败");  
    }  


}
