import java.text.SimpleDateFormat;  
import java.util.Date;  

public class Test01  {  
    public static void main(String[] args) throws Exception{  
        //获取当前时间,采用无参构造对象  
        Date nowTime=new Date();  
        System.out.println(nowTime);//Sat Jul 23 16:43:44 CST 2022  

        //日期格式化 然后输出  
        //SimpleDateFormat  
        SimpleDateFormat sdf1=new SimpleDateFormat("yyyy-MM-dd HH:mm:ss SS");  
        String nowTimestr=sdf1.format(nowTime);  
        System.out.println(nowTimestr); //2022-07-23 16:53:53 663  

        //String -->Date类型  
        String time1="2022-01-23 16:53:45 663";  
        SimpleDateFormat sdf2=new SimpleDateFormat("yyyy-MM-dd HH:mm:ss SS");//必须要与转换的格式相互对应,不然会报错,在main方法上面添加throws Exception  
        Date d2=sdf2.parse(time1);  
        System.out.println(d2);//Sun Jan 23 16:53:45 CST 2022  

    }  
}
