      <script>
          var filelength = 1;// 文件个数

          // var imgsrcFront="data:" + "image/png" + ";base64,";//src值：dataurl的前缀
          var imgsrcFront = "";
          var imgnum = 1;//信号量
          // 获取左按钮节点
          var lbtn = document.getElementById("lbtn");
          // 获取右边按钮节点
          var rbtn = document.getElementById("rbtn");
          var imgg = document.getElementById("imgg");//图片框的对象

          // 左边按钮
          lbtn.onclick = function () {
              imgnum;
              if (imgnum < 0) {
                  imgnum = 0;
              }
              console.log("imgnum" + imgnum)
              var reader = new FileReader();
              // 获取文件以及内容
              var fileList = choosefile();
              reader.readAsText(fileList[imgnum], "UTF8");
              reader.onload = function (e) {
                  content = e.target.result;
                  imgg.src = imgsrcFront + content
              }
              nowOn(imgnum, choosefile().length);//显示当前的图片序号
          }

          //获取已经加载的所有文件
          function choosefile() {
              filelength = document.getElementById('files').files.length; //将文件总数给全局变量
              // console.log("总共个数：" + filelength);
              return document.getElementById('files').files;
          }

          // 显示当前的图文序号
          function nowOn(now, size) {
              var a = document.getElementById("nowOn");
              a.innerText = (now + 1) + "/" + size;
          }

          // 右边按钮时间
          rbtn.onclick = function () {
              nextImg();
              // var fd = choosefile();
              // // console.log("获取全部的" + fd.length)
              // imgnum++;
              // if (imgnum >= filelength) {
              //     imgnum = 0;
              // }
              // console.log("imgnum" + imgnum)
              // var reader = new FileReader();
              // // 获取文件以及内容
              // var fileList = choosefile();
              // reader.readAsText(fileList[imgnum], "UTF8");
              // reader.onload = function (e) {
              //     content = e.target.result;
              //     imgg.src = imgsrcFront + content
              // }
              // nowOn(imgnum, choosefile().length);
          }

          // 文件转成base64并下载
          function toBase64() {
              var fileList = document.getElementById('files2');
              for (var i = 0; i < fileList.files.length; i++) {
                  const file = fileList.files[i];
                  const reader = new FileReader();
                  reader.onload = function (e) {
                      const base64 = e.target.result;
                      //   document.querySelector('#image').src = base64;
                      var blob = new Blob([base64], { type: 'text/plain' });

                      // Create a link element and use the Blob URL as the download URL
                      var a = document.createElement('a');
                      // 采用原名称+随机数命名
                      var aNum = Math.floor(Math.random(1, 100) * 100);
                      console.log(aNum);
                      // a.download =file.name.substring(0, file.name.lastIndexOf("."))+""+aNum+".sm" ;
                      a.download = file.name + aNum + ".ccc"
                      a.href = window.URL.createObjectURL(blob);
                      a.click();
                  }
                  reader.readAsDataURL(file);
              }
          }

          // 下一个图片
          function nextImg() {
              var fd = choosefile();
              // console.log("获取全部的" + fd.length)
              imgnum++;
              if (imgnum >= filelength) {
                  imgnum = 0;
              }
              console.log("imgnum" + imgnum)
              var reader = new FileReader();
              // 获取文件以及内容
              var fileList = choosefile();
              reader.readAsText(fileList[imgnum], "UTF8");
              reader.onload = function (e) {
                  content = e.target.result;
                  imgg.src = imgsrcFront + content
              }
              nowOn(imgnum, choosefile().length);
          }
      </script>

      </html>
